package com.company;public class Product {    private String title;    private double price;    private String color;    public Product(String title, double price, String color) {        this.title = title;        this.price = price;        this.color = color;    }    public Product() {    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public double getPrice() {        return price;    }    public void setPrice(double price) {        this.price = price;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    @Override    public String toString() {        return "Product{" +                "title='" + title + '\'' +                ", price=" + price +                ", color='" + color + '\'' +                '}';    }}