package com.company.State;import com.company.User;public class NewOrder implements Activity{    @Override    public void sendProduct(User user) {        System.out.println("-- Status: new order");        System.out.println("Your basket: ");        for (int i = 0; i < user.getBasket().size() ; i++) {            System.out.println(i + 1 + ".) " + user.getBasket().get(i).toString());        }    }}